import React, { useState } from "react";
import { withAuth } from "../lib/AuthProvider";
import axios from "axios";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [listOfUsers, setListOfUsers] = useState([]);
  const [messageErrPassword, setMessageErrPassword] = useState("");
  const [messageErrUser, setMessageErrUser] = useState("");

  // state = {
  //   username: "",
  //   password: "",
  //   listOfUsers: [],
  //   messageErrUser: "",
  //   messageErrPassword: "",
  // };

  // componentDidMount = () => {
  //   getAllUsers();
  // };
  let getAllUsers = async () => {
    axios.get(process.env.REACT_APP_API_URI + `/api/allprofiles`);
    await ((responseFromApi) => {
      setListOfUsers(responseFromApi.data);
    });
  };

  let handleFormSubmit = (event) => {
    event.preventDefault();
    const { username, password } = state;
    //console.log('Login -> form submit', { username, password });
    let errorMessage = state.listOfUsers.findIndex(
      (user) => username === user.username
    );
    if (errorMessage === -1) {
      setMessageErrUser(
        errorMessage === -1 ? (
          <span className="text-danger">User doesn't exist</span>
        ) : (
          <span></span>
        )
      );
    } else {
      props.login({ username, password });
    }

    let errorPassword = listOfUsers.findIndex(
      (user) => password === user.password
    );
    console.log(errorPassword, "errroPassword");

    if (errorPassword === -1) {
      setState({
        messageErrPassword:
          errorPassword === -1 ? (
            <span className="text-danger">Wrong Password</span>
          ) : (
            <span></span>
          ),
      });
    } else {
      props.login({ username, password });
    }
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    setState({ [name]: value });
  };

  return (
    <div className="fondoApp position-absolute">
      <div className=" m-3 padLog">
        <div className="loginsignup-form">
          <form onSubmit={handleFormSubmit}>
            <div className="form-group">
              <input
                required
                type="text"
                className="form-control formLog"
                name="username"
                placeholder="Username"
                value={username}
                onChange={handleChange}
              />
              {state.messageErrUser}
            </div>
            <div className="form-group">
              <input
                required
                type="password"
                name="password"
                placeholder="Password"
                className="form-control formLog"
                value={password}
                onChange={handleChange}
              />
            </div>
            {state.messageErrPassword}

            <div className="text-center pt-3">
              <input type="submit" value="Log in" className="btnBlueLog" />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default withAuth(Login);
