import React, { useState } from "react";
import axios from "axios";
import service from "../api/service";

const AddCreation = (props) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");

  let fileUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("imgPath", e.target.files[0]);
    try {
      const res = await axios.post(
        "http://localhost:4000/api/upload",
        formData
      );
      creation.imgPath = res.data.secure_url;
    } catch (err) {
      console.log(err);
    }
  };

  let onChangeTitle = (e) => {
    creation.title = e.target.value;
  };
  let onChangeDescription = (e) => {
    creation.description = e.target.value;
  };

  let onChangeCategory = (e) => {
    creation.category = e.target.value;
  };

  let handleSubmit = async (e) => {
    e.preventDefault();
    console.log(creation);
    await service.uploadCreation(creation);
    props.history.push("/private/profile");
    console.log("Creation Added!");
  };

  return (
    <div onSubmit={(e) => handleSubmit(e)}>
      <h1 class="text-center">Create a new Product:</h1>
      <form>
        <div class="form-group">
          <label for="formGroupExampleInput2">Image</label>
          <input
            type="file"
            name="imgPath"
            class="form-control"
            id="imageInput"
            placeholder="Upload Img"
            onChange={(e) => fileUpload(e)}
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput">Title</label>
          <input
            type="text"
            name="title"
            class="form-control"
            id="titleInput"
            placeholder="title"
            onChange={(event) => setTitle(event.target.value)}
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput2">Description</label>
          <textarea
            type="text"
            name="description"
            class="form-control"
            id="descriptionInput"
            placeholder="Description..."
            onChange={(event) => setTitle(event.target.value)}
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput2">Category</label>
          <select
            name="category"
            type="text"
            name="description"
            class="form-control text-center"
            id="categoryInput"
            placeholder="Category"
            onChange={onChangeCategory}
          >
            <option value="drawings">Drawings</option>
            <option value="handmade">Handmade</option>
            <option value="wood">Wood</option>
            <option value="photography">Photography</option>
          </select>
        </div>
        <div>
          <button class="btn btn-primary" type="submit">
            {" "}
            Create!{" "}
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddCreation;
