import React, { useState } from "react";
import axios from 'axios';


const AddCreation = () => {

    const [creation, setCreation] = useState({});

      let fileUpload = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("imgPath", e.target.files[0]);
        try {
          const res = await axios.post(
            "http://localhost:4000/api/upload",
            formData
          );
         creation.imgPath = res.data.secure_url;
        } catch (err) {
          console.log(err);
        }
      };


      let handleSubmit = async (e) => {
        e.preventDefault();
        console.log(creation)
        await service.profileUpdate(creation);
        props.history.push("/private/profile");
        console.log("Creation Added!");
      };



  return (
    <div>
      <h1 class="text-center">Create a new Product:</h1>
      <form>
      <div class="form-group">
          <label for="formGroupExampleInput2">Image</label>
          <input
            type="file"
            name="imgPath"
            class="form-control"
            id="imageInput"
            placeholder="Upload Img"
            onChange={(e) => fileUpload(e)}
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput">Title</label>
          <input
            type="text"
            name="title"
            class="form-control"
            id="titleInput"
            placeholder="title"
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput2">Description</label>
          <textarea
            type="text"
            name="description"
            class="form-control"
            id="descriptionInput"
            placeholder="Description..."
          />
        </div>
        <div class="form-group">
          <label for="formGroupExampleInput2">Category</label>
          <select name="category" type="text" name="description" class="form-control text-center" id="categoryInput" placeholder="Category">
        <option value="drawings">Drawings</option>
        <option value="handmade">Handmade</option>
        <option value="wood">Wood</option>
        <option value="photography">Photography</option>
         </select>
        </div>
        <div>
        <button class="btn btn-primary" type="submit"> Create! </button>
        </div>
      </form>
    </div>
  );
};

export default AddCreation;
