import React, { useState, useEffect } from "react";
import service from "../api/service";
import { withAuth } from "../lib/AuthProvider";
import { Link } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const [finalUser, setUserData] = useState({});
  const [myCreations, setMyCreations] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const UserfromDB = await service.getUserInfo();
      const creations = await service.getProductInfo();
      console.log(creations, "creaciones")
      setUserData(UserfromDB);
      setMyCreations(creations)
    };
    fetchData();
  }, []);



  myCreations.haveList.map(creation => {
    <div>
      <img src={creation.imgPath} alt=""/>
      <h3>{creation.title}</h3>
    </div>
  })
  return (
    <div>
      <Link className="btn btn-primary" to="/private/edit-profile">
        Edit Profile
      </Link>
      <div className="row d-flex justify-content-center align-items-center">
        <div className="col">
          <img className="profileImg" src={finalUser.imgPath} alt="perfil" />
        </div>
        <div className="col">
          <h3><b>{finalUser.name} {finalUser.lastName}</b></h3>
        </div>
      </div>
      <Link className="btn btn-dark" to="/private/creation-form">
        Upload Creation
      </Link>




    </div>
  );
};

export default withAuth(Profile);
