import React, { useState, useEffect } from "react";
import axios from "axios";

const ProductDetails = (props) => {
  const [details, setDetails] = useState();

  useEffect(() => {
    const fetchData = async () => {
      const { params } = props.match;
      const InfofromDB = await axios.get(
        `http://localhost:4000/api/product/${params.id}`
      );
      setDetails(InfofromDB);
      console.log(params, "params");
    };
    fetchData();
  }, []);

  const showDetails = details ?      ( <div><img src={details.imgPath} alt=""/>
  <h5>{details.title}</h5>
  <p>{details.description}</p></div>)  : <div></div>;

  console.log(details, "details");
  return (
    <div>
      <img src={details.imgPath} alt=""/>
            <h5>{details.title}</h5>
            <p>{details.description}</p>
    </div>
  );
};

export default ProductDetails;
