import React, { useState, useEffect } from "react";
import service from "../api/service";
import { withAuth } from "../lib/AuthProvider";
import { Link } from "react-router-dom";

const Profile = () => {
  const [finalUser, setUserData] = useState({});
  const [myCreations, setMyCreations] = useState();
  const [buttonOn, setButtonOn] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const UserfromDB = await service.getUserInfo();
      const creations = await service.getMyProducts();
      setUserData(UserfromDB);
      setMyCreations(creations);
    };
    fetchData();
  }, []);

    // monitor the state of the toggle
  // add/remove click event handler to the document
  useEffect(() => {
    const clickHandler = ({ target }) => {
      const container = document.getElementById(`container-${id}`);
      if (container.contains(target)) return;
      setOpen(false);
    };

    document.addEventListener("click", clickHandler);

    // these functions clean up the event listeners
    return () => document.removeEventListener("click", clickHandler);
  });

  // same but for keypresses
  // if the esc key is pressed close the toggles
  useEffect(() => {
    const keyHandler = ({ keyCode }) => {
      if (keyCode !== 27) return;
      setButtonOn(false);
    };
    document.addEventListener("keydown", keyHandler);

    return () => document.removeEventListener("keydown", keyHandler);
  });

  const displayHaveList = myCreations ? (
    myCreations.haveList.map((creation) => {
      return (
        <div key={creation._id} className="col haveListCard">
          <img src={creation.imgPath} alt="" />
          <p>{creation.title}</p>
        </div>
      );
    })
  ) : (
    <div></div>
  );

  const displayWantList = myCreations ? (
    myCreations.wantList.map((wantedCreations) => {
      return (
        <div key={wantedCreations._id} className={buttonOn ? " col haveListCard show" : "col haveListCard"} >
          <img src={wantedCreations.imgPath} alt="" />
          <p>{wantedCreations.title}</p>
        </div>
      );
    })
  ) : (
    <div></div>
  );

  return (
    <div>
      <Link className="btn btn-primary" to="/private/edit-profile">
        Edit Profile
      </Link>
      <div className="row d-flex align-items-center mt-3">
        <div className="col">
          <img className="profileImg" src={finalUser.imgPath} alt="perfil" />
        </div>
        <div className="col">
          <h5>
            <b>
              {finalUser.name} {finalUser.lastName}
            </b>
          </h5>
        </div>
      </div>
      <Link className="btn addCreation-btn m-4" to="/private/creation-form">
        Upload Creation
      </Link>
      <div id={id} className="row">
        <div className="col btn-group" role="group">
          {/* {actived ? displayHaveList : <div></div>} */}
          <button
            aria-expanded={buttonOn === true ? "true" : "false"}
            className={buttonOn === true ? "btn btn-dark active" : "btn btn-dark"}
            type="button"
            onClick={() => setButtonOn(!buttonOn)}
          >
            My Creations
          </button>
        </div>
        {/* {actived ? displayWantList : <div></div>} */}
        <div className="col">
          <button
            type="button"
            onClick={() => setButtonOn(!buttonOn)}
            aria-expanded={buttonOn === true ? "true" : "false"}
            className={buttonOn === true ? "btn btn-dark active" : "btn btn-dark"}
          >
            Wanted Creations
          </button>
        </div>
      </div>

      <div className="row haveList d-flex justify-content-center">
        {displayHaveList}
      </div>
      <div className="row haveList d-flex justify-content-center">
        {displayWantList}
      </div>
    </div>
  );
};

export default withAuth(Profile);
