import React, { useState, useEffect } from "react";
import service from "../api/service";
import { withAuth } from "../lib/AuthProvider";
import { Link } from "react-router-dom";

const Discover = (props) => {
    const [allProducts, setAllProducts] = useState();

    useEffect(() => {
      const fetchData = async () => {
        const products = await service.getAllTheProducts();
        setAllProducts(products);
      };
      fetchData();
    }, []);

    const displayAllCreations = allProducts ? (
        allProducts.map((product) => {

           if (product.creator !== props.user._id){
          return (
              <Link to= {`/product/${product._id}`}>
            <div key={product._id} className="col creationsCard">
              <img src={product.imgPath} alt="" />
              <h3>{product.title}</h3>
            </div>
            </Link>
          );
        }else{
            return 
            <div>Nothing to check...</div>
        }})
      ) : (
        <div></div>
      );

    return (
        <div className="row">
            {displayAllCreations}
        </div>
    )
}

export default withAuth(Discover)
